import React, { useState } from 'react';
import './App.css';

function App() {
  const [selected, setSelected] = useState(null);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [status, setStatus] = useState({});

  const handleSelect = (number) => {
    if (!status[number]) setSelected(number);
  };

  const handleReserve = () => {
    if (selected && name && phone) {
      setStatus({ ...status, [selected]: { name, phone, status: 'reservado' } });
    }
  };

  const whatsappLink = `https://wa.me/SEUNUMEROWHATSAPP?text=Olá, sou ${name}, reservei o número ${selected} e estou enviando o comprovante de pagamento.`;

  return (
    <div className="App">
      <h1>Rifa Solidária</h1>
      <div className="grid">
        {[...Array(200)].map((_, i) => {
          const num = i + 1;
          return (
            <button
              key={num}
              className={status[num]?.status === 'reservado' ? 'reserved' : 'available'}
              onClick={() => handleSelect(num)}
              disabled={!!status[num]}
            >
              {num}
            </button>
          );
        })}
      </div>

      {selected && !status[selected] && (
        <div className="form">
          <h2>Número escolhido: {selected}</h2>
          <input placeholder="Seu nome" value={name} onChange={(e) => setName(e.target.value)} />
          <input placeholder="Seu telefone" value={phone} onChange={(e) => setPhone(e.target.value)} />
          <button onClick={handleReserve}>Reservar número</button>
        </div>
      )}

      {status[selected]?.status === 'reservado' && (
        <div className="pagamento">
          <h2>Pagamento</h2>
          <p>Chave PIX: <strong>sua-chave@pix.com</strong></p>
          <img src="/qrcode-pix.png" alt="QR Code PIX" width="200" />
          <a href={whatsappLink} target="_blank" rel="noopener noreferrer">
            <button>Enviar comprovante via WhatsApp</button>
          </a>
        </div>
      )}
    </div>
  );
}

export default App;
